mapper.insert:
INSERT INTO users (
        nickname
        , email
        , `password`
        , department)
VALUES(
        #{nickname}
        ,#{email}
        ,#{password}
        ,#{department}
)

INSERT INTO admins (
        nickname
        , email
        , `password`
        , department)
VALUES(
        #{nickname}
        ,#{email}
        ,#{password}
        ,#{department}
)

INSERT INTO login_history(
users_id
,admin_yn
,login_at
)
VALUES(
#{id}
, #{isAdmin}
, now()
)

mapper.select:
SELECT id
FROM admins
WHERE email = #{email}

SELECT id
FROM users
WHERE email = #{email}
